INSERT INTO CLASIFICACION VALUES(SQ_CLASIFICACION_ID.NEXTVAL,'NINJAGO','LEGO® NINJAGO se basa en una popular serie de televisión en la que un equipo de valientes ninjas lucha contra los villanos que intentan destruir su mundo. A tu hijo le encantará jugar en los combates de Spinjitzu con sus amigos, una posibilidad que aporta al juego aspectos sociales. Como es habitual, con LEGO la construcción es solo parte de la diversión: estos fascinantes y llamativos sets también estimulan su mente e imaginación. Tu hijo lo pasará¡ en grande construyendo los sets e imaginando aventuras. ¡Ayúdale a demostrar que puede convertirse en un Maestro del Spinjitzu!','N','TEMA',IMAGENES_NT(IMAGEN('COVER',IMPORTAR_BINARIO('ninjago.png')),IMAGEN('LOGO',IMPORTAR_BINARIO('LEGO_ninjago.png'))));
INSERT INTO CLASIFICACION VALUES(SQ_CLASIFICACION_ID.NEXTVAL,'SPEED CHAMPIONS','LEGO® Speed Champions es una colección de autos fáciles de armar, auténticos y realistas de marcas famosas como McLaren, Porsche y Ferrari. Los niños pueden sobrepasar a otros corredores en algunos de los más rápidos y poderosos vehiculos en el mundo, competir en desafiantes carreras, preparar el equipo para los pits, y celebrar la toma de la bandera a cuadros en el podio con sets detallados que recrean el universo de las carreras. LEGO Speed Champions le permite a los niños conducir los autos más geniales de clases como Super Autos, Monoplazas, y GT; y es la manera perfecta de poner a correr el pulso!','N','TEMA',IMAGENES_NT(IMAGEN('COVER',IMPORTAR_BINARIO('speedchampions.png')),IMAGEN('LOGO',IMPORTAR_BINARIO('LEGO_SpeedChampions.png'))));
